sparql:
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?protein ?mnemonic ?label ?organism WHERE {
  BIND(<http://purl.uniprot.org/uniprot/P12345> AS ?protein)
  OPTIONAL { ?protein up:mnemonic ?mnemonic }
  OPTIONAL { ?protein rdfs:label ?label }
  OPTIONAL { ?protein up:organism ?organism }
}

PREFIX up: <http://purl.uniprot.org/core/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?protein ?proteinLabel ?annotation ?goTerm
WHERE {
  ?protein a up:Protein ;
           rdfs:label ?proteinLabel ;
           up:annotation ?annotation .
  ?annotation a up:Annotation ;
              up:classifiedWith ?goTerm .
  FILTER(CONTAINS(LCASE(?proteinLabel), "insulin"))
  FILTER(STRSTARTS(STR(?goTerm), "http://purl.obolibrary.org/obo/GO_"))
}

PREFIX up: <http://purl.uniprot.org/core/>
PREFIX taxon: <http://purl.uniprot.org/taxonomy/>

SELECT ?protein ?label
WHERE {
  ?protein a up:Protein .
  ?protein up:reviewed true .
  ?protein up:organism taxon:9606 .
  OPTIONAL { ?protein rdfs:label ?label }
}
