sparql:
# This SPARQL query would retrieve GO annotations for insulin
# IF querying the UniProt DATA graph (not this schema ontology)
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?protein ?goTerm ?goLabel ?aspect
WHERE {
    ?protein a up:Protein ;
             rdfs:label ?proteinLabel ;
             up:classifiedWith ?goTerm .

    # Filter for insulin (would need actual insulin IRI or label pattern)
    FILTER(CONTAINS(LCASE(?proteinLabel), "insulin"))

    # GO term details
    ?goTerm rdfs:label ?goLabel .

    # Optionally get GO aspect (molecular function, biological process, cellular component)
    OPTIONAL { ?protein up:cellularComponent ?goTerm . BIND("cellular component" AS ?aspect) }
}

PREFIX up: <http://purl.uniprot.org/core/>
PREFIX taxon: <http://purl.uniprot.org/taxonomy/>

SELECT ?protein
WHERE {
  ?protein a up:Reviewed_Protein ;
           up:organism taxon:9606 .
}
