# This query WOULD work on a UniProt data endpoint (not this schema ontology):
PREFIX up: <http://purl.uniprot.org/core/>

SELECT ?protein ?mnemonic ?organism ?sequence WHERE {
  ?protein a up:Protein .
  FILTER(CONTAINS(STR(?protein), "P12345"))
  OPTIONAL { ?protein up:mnemonic ?mnemonic }
  OPTIONAL { ?protein up:organism ?organism }
  OPTIONAL { ?protein up:sequence ?seq . ?seq rdf:value ?sequence }
}

PREFIX up: <http://purl.uniprot.org/core/>

SELECT ?protein ?proteinLabel ?annotation ?goTerm ?goLabel
WHERE {
    ?protein a up:Protein ;
             rdfs:label ?proteinLabel ;
             up:annotation ?annotation .

    # Filter for insulin (would need to match on label or recommended name)
    FILTER(CONTAINS(LCASE(?proteinLabel), "insulin"))

    # Get GO annotations
    ?annotation a up:Function_Annotation ;
                up:classifiedWith ?goTerm .

    OPTIONAL { ?goTerm rdfs:label ?goLabel }
}

PREFIX up: <http://purl.uniprot.org/core/>

SELECT ?protein
WHERE {
  ?protein a up:Reviewed_Protein ;
           up:organism <http://purl.uniprot.org/taxonomy/9606> .
}
