PREFIX up: <http://purl.uniprot.org/core/>
SELECT ?protein ?disease
WHERE {
  ?protein a up:Protein ;
           up:annotation ?annotation .
  ?annotation a up:Disease_Annotation ;
              up:disease ?disease .
}

PREFIX up: <http://purl.uniprot.org/core/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT ?protein ?disease ?diseaseName
WHERE {
  ?protein a up:Protein .
  ?protein up:annotation ?diseaseAnnotation .
  ?diseaseAnnotation a up:Disease_Annotation .
  ?diseaseAnnotation up:disease ?disease .
  OPTIONAL { ?disease skos:prefLabel ?diseaseName }
}

PREFIX up: <http://purl.uniprot.org/core/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

SELECT ?protein ?proteinLabel ?disease ?diseaseName
WHERE {
  ?protein a up:Protein .
  ?protein up:annotation ?annotation .
  ?annotation a up:Disease_Annotation .
  ?annotation up:disease ?disease .
  OPTIONAL { ?protein rdfs:label ?proteinLabel }
  OPTIONAL { ?disease skos:prefLabel ?diseaseName }
}

PREFIX up: <http://purl.uniprot.org/core/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?protein ?proteinLabel ?disease ?diseaseLabel
WHERE {
  ?protein a up:Protein .
  ?protein up:annotation ?annotation .
  ?annotation a up:Disease_Annotation .
  ?annotation up:disease ?disease .
  OPTIONAL { ?protein up:mnemonic ?proteinLabel . }
  OPTIONAL { ?disease rdfs:label ?diseaseLabel . }
}
