sparql:
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT DISTINCT ?property ?propertyType ?label ?comment ?domain
WHERE {
  {
    # Properties with explicit domain
    ?property rdfs:domain up:Protein .
    OPTIONAL { ?property rdf:type ?propertyType }
  }
  UNION
  {
    # Properties actually used with Protein instances
    ?protein a up:Protein .
    ?protein ?property ?value .
    OPTIONAL { ?property rdf:type ?propertyType }
    OPTIONAL { ?property rdfs:domain ?domain }
  }
  OPTIONAL { ?property rdfs:label ?label }
  OPTIONAL { ?property rdfs:comment ?comment }
}
ORDER BY ?property
LIMIT 100

PREFIX up: <http://purl.uniprot.org/core/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT DISTINCT ?annotationType ?label WHERE {
    ?annotationType rdfs:subClassOf up:Annotation .
    OPTIONAL { ?annotationType rdfs:label ?label }
}
ORDER BY ?annotationType

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX up: <http://purl.uniprot.org/core/>

SELECT ?protein ?label ?description ?comment ?superClass
WHERE {
  ?protein a owl:Class .
  ?protein rdfs:label ?label .
  FILTER(str(?label) = "Protein")
  OPTIONAL { ?protein dcterms:description ?description }
  OPTIONAL { ?protein rdfs:comment ?comment }
  OPTIONAL { ?protein rdfs:subClassOf ?superClass }
}

PREFIX up: <http://purl.uniprot.org/core/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX owl: <http://www.w3.org/2002/07/owl#>

SELECT DISTINCT ?annotationType ?label WHERE {
  ?annotationType rdfs:subClassOf* up:Annotation .
  ?annotationType a owl:Class .
  OPTIONAL { ?annotationType rdfs:label ?label }
}
ORDER BY ?label

PREFIX up: <http://purl.uniprot.org/core/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT ?protein ?annotation ?gene ?location ?domain ?component ?interaction ?classification
WHERE {
  ?protein a up:Protein .

  OPTIONAL { ?protein up:annotation ?annotation }
  OPTIONAL { ?protein up:encodedBy ?gene }
  OPTIONAL { ?protein up:encodedIn ?location }
  OPTIONAL { ?protein up:domain ?domain }
  OPTIONAL { ?protein up:component ?component }
  OPTIONAL { ?protein up:interaction ?interaction }
  OPTIONAL { ?protein up:classifiedWith ?classification }
}
LIMIT 100

