PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT DISTINCT ?property ?range
WHERE {
  ?property rdfs:domain up:Protein .
  OPTIONAL { ?property rdfs:range ?range }
}
ORDER BY ?property

